import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { User } from 'src/models/user.model';
import { AuthService } from 'src/services/auth/auth.service';
import { UserService } from 'src/services/user/user.service';

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.scss']
})
export class UsersComponent implements OnInit {
  users: User[] = [];
  errorMessage = '';

  constructor(
    private userService: UserService,
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit(): void {
    if (this.hasPermission('can_read_users')) {
      this.loadUsers();
    }
  }

  loadUsers(): void {
    this.userService.getAllUsers().subscribe({
      next: (data) => this.users = data,
      error: (err) => this.errorMessage = 'Greska pri ucitavanju korisnika'
    });
  }

  hasPermission(permission: string): boolean {
    console.log(permission + ' --- ' + this.authService.hasPermission(permission));
    return this.authService.hasPermission(permission);
  }

  deleteUser(id: number): void {
    if (confirm('Da li ste sigurni?')) {
      this.userService.deleteUser(id).subscribe({
        next: () => this.loadUsers(),
        error: (err) => this.errorMessage = 'Greska pri brisanju korisnika'
      });
    }
  }

  formatPermission(perm: string): string {
    return perm.replace('can_', '').replace(/_/g, ' ');
  }
}
<div class="container">
      <div style="display: flex; justify-content: space-between; align-items: center;">
        <h1>Korisnici</h1>
        <a routerLink="/users/new" class="btn btn-primary" *ngIf="hasPermission('can_create_users')">
          Dodaj korisnika
        </a>
      </div>

      <div *ngIf="errorMessage" class="alert alert-danger">{{errorMessage}}</div>
      <div *ngIf="!hasPermission('can_read_users')" class="alert alert-danger">
        Nemate dozvolu za citanje korisnika
      </div>

      <table *ngIf="hasPermission('can_read_users')">
        <thead>
          <tr>
            <th>Ime</th>
            <th>Prezime</th>
            <th>Email</th>
            <th>Dozvole</th>
            <th *ngIf="hasPermission('can_update_users') || hasPermission('can_delete_users')">Akcije</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let user of users">
            <td>{{user.firstName}}</td>
            <td>{{user.lastName}}</td>
            <td >
              <a *ngIf="hasPermission('can_update_users')" 
                 [routerLink]="['/users', user.id, 'edit']" 
                 style="color: #667eea; cursor: pointer;">
                {{user.email}}
              </a>
              <span *ngIf="!hasPermission('can_update_users')">{{user.email}}</span>
            </td>
            <td style="width: 450px;">
              <span class="badge badge-primary" *ngFor="let perm of user.permissions">
                {{formatPermission(perm)}}
              </span>
            </td>
            <td *ngIf="hasPermission('can_update_users') || hasPermission('can_delete_users')">
              <button class="btn btn-danger" 
                      *ngIf="hasPermission('can_delete_users')"
                      (click)="deleteUser(user.id)">
                Delete
              </button>
              <button class="btn btn-danger" 
                      *ngIf="hasPermission('can_update_users')"
                      (click)="updateUser(user.id)">
                Update
              </button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
